mapper.select:
select da.id as "depositId",da.CREATE_DATE,da.del_flag,da.update_by,tt.house_id,da.agreement_status as "bookStatus",tt.short_desc, tt.short_location,tt.attachment_path,ct.cell_phone from T_DEPOSIT_AGREEMENT da left join ( select DEPOSIT_AGREEMENT_ID,te.cell_phone from t_contract_tenant cte,t_tenant te where cte.tenant_id=te.id and cte.del_flag='0' and te.del_flag='0' ) ct on da.id=ct.deposit_agreement_id left join ( select p.project_name,b.building_name,h.id house_id,h.house_code,'' room_no,h.short_desc,h.short_location,a.attachment_path from t_house h, t_property_project p,t_building b,t_attachment a where h.property_project_id=p.id and h.building_id=b.id and h.id=a.house_id union all select p.project_name,b.building_name,r.id house_id,h.house_code,r.room_no,r.short_desc,r.short_location,a.attachment_path from t_room r, t_property_project p,t_building b,t_house h,t_attachment a where r.property_project_id=p.id and r.building_id=b.id and r.house_id=h.id and r.id=a.room_id ) tt on ((da.house_id=tt.house_id and da.rent_mode='0') or da.room_id=tt.house_id) where ct.cell_phone = #{userPhone} and da.data_source='2'
AND tt.house_id = #{houseId}
order by create_date desc

SELECT 

FROM t_contract_book a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.user_name = #{userName}


		AND a.user_phone = #{userPhone}


		AND date_format(a.book_date,'%Y-%m-%d') = date_format(#{bookDate},'%Y-%m-%d')


		AND a.book_status = #{bookStatus}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM t_contract_book a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_contract_book SET 	
	book_status = #{bookStatus}
WHERE house_id = #{houseId} or room_id = #{houseId}

UPDATE t_contract_book SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE house_id = #{houseId} or room_id = #{roomId}
