mapper.select:
SELECT 

FROM t_electric_fee a

WHERE a.id = #{id}

SELECT 

FROM t_electric_fee a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.rent_contract_id = #{rentContractId}


		AND a.settle_type = #{settleType}


		AND a.start_date = #{startDate}


		AND a.end_date = #{endDate}


		AND a.meter_value = #{meterValue}


		AND a.person_fee = #{personFee}


		AND a.muliti_fee = #{mulitiFee}


		AND a.settle_status = #{settleStatus}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM t_electric_fee a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_electric_fee SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
