mapper.select:
SELECT 

FROM t_receipt a

WHERE a.id = #{id}

select t.*,case when t.tradeType=1 then ta.agreement_name 
when t.tradeType=3 or t.tradeType=7 or t.tradeType=6 or t.tradeType=8 then tc.contract_name
else "" end tradeName from(
SELECT 

FROM t_receipt a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.trading_accounts_id = #{tradingAccounts.id}


		AND a.receipt_no LIKE 
			'%'||#{receiptNo}||'%'
			'%'+#{receiptNo}+'%'
			concat('%',#{receiptNo},'%')


		AND a.receipt_date = #{receiptDate}


		AND a.receipt_amount = #{receiptAmount}


		AND a.trade_mode = #{tradeMode}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC


) t left join t_deposit_agreement ta on t.trade_id=ta.id 
	left join t_rent_contract tc on t.trade_id=tc.id 


	AND t.tradeName like concat('%',#{tradeName},'%')

SELECT 

FROM t_receipt a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_receipt SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
