mapper.select:
SELECT 

FROM t_devices a

WHERE a.id = #{id}

SELECT 

FROM t_devices a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.device_id = #{deviceId}


		AND a.device_name LIKE 
			'%'||#{deviceName}||'%'
			'%'+#{deviceName}+'%'
			concat('%',#{deviceName},'%')


		AND a.device_model = #{deviceModel}


		AND a.device_type = #{deviceType}


		AND a.device_price = #{devicePrice}


		AND a.device_brand LIKE 
			'%'||#{deviceBrand}||'%'
			'%'+#{deviceBrand}+'%'
			concat('%',#{deviceBrand},'%')


		AND a.device_amount = #{deviceAmount}


		AND a.device_status = #{deviceStatus}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM t_devices a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_devices SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
