mapper.select:
SELECT 
	
FROM t_house a

WHERE a.id = #{id}

SELECT 
	
FROM t_house a


	a.del_flag = #{DEL_FLAG_NORMAL}
	
		AND a.property_project_id = #{propertyProject.id}
	
	
		AND a.building_id = #{building.id}
	
	
		AND a.owner_id = #{owner.id}
	
	
		AND a.house_no LIKE 
			'%'||#{houseNo}||'%'
			'%'+#{houseNo}+'%'
			concat('%',#{houseNo},'%')
	
	
		AND a.house_status = #{houseStatus}
	


	
		ORDER BY ${page.orderBy}
	
	
		ORDER BY a.update_date DESC

SELECT 
	
FROM t_house a


	a.del_flag = #{DEL_FLAG_NORMAL}
		

	
		ORDER BY ${page.orderBy}
	
	
		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_house SET property_project_id = #{propertyProject.id}, building_id = #{building.id}, owner_id = #{owner.id}, house_no = #{houseNo}, house_floor = #{houseFloor}, house_space = #{houseSpace}, decoration_spance = #{decorationSpance}, house_structure = #{houseStructure}, decoration_structure = #{decorationStructure},
house_status = #{houseStatus},
update_by = #{updateBy.id}, update_date = #{updateDate}, remarks = #{remarks} WHERE id = #{id}

UPDATE t_house SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
