mapper.select:
SELECT 

FROM t_partner a

WHERE a.id = #{id}

SELECT 

FROM t_partner a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.partner_type = #{partnerType}


		AND a.partner_name LIKE 
			'%'||#{partnerName}||'%'
			'%'+#{partnerName}+'%'
			concat('%',#{partnerName},'%')


		AND a.cell_phone = #{cellPhone}


		AND a.desk_phone = #{deskPhone}


		AND a.user_name LIKE 
			'%'||#{userName}||'%'
			'%'+#{userName}+'%'
			concat('%',#{userName},'%')


		AND a.bank_name LIKE 
			'%'||#{bankName}||'%'
			'%'+#{bankName}+'%'
			concat('%',#{bankName},'%')


		AND a.bank_account = #{bankAccount}


		AND a.commission_percent = #{commissionPercent}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM t_partner a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_partner SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
