mapper.select:
select sales,project_name,building_name,house_no,start_date,expired_date, coalesce(sum(trans_amount),0) trans_amount,payment_type,coalesce(sum(shuidianya_fee),0) shuidianya_fee, coalesce(sum(fangzuya_fee),0) fangzuya_fee,coalesce(sum(fangzu_fee),0) fangzu_fee,coalesce(sum(weiyue_fee),0) weiyue_fee from ( select sales,project_name,building_name,house_no,start_date,expired_date, trans_amount,payment_type, case when payment_type='2' then trans_amount end shuidianya_fee, case when payment_type='4' then trans_amount end fangzuya_fee, case when payment_type='6' then trans_amount end fangzu_fee, case when payment_type='9' then trans_amount end weiyue_fee from( select u.name sales,p.PROJECT_NAME,b.BUILDING_NAME,h.HOUSE_no,a.start_date,a.expired_date, a.trans_amount,a.payment_type from t_payment_trans a left join t_rent_contract c on a.trans_id=c.id left join T_PROPERTY_PROJECT p on c.PROPERTY_PROJECT_ID=p.id left join T_BUILDING b on c.BUILDING_ID=b.id left join t_house h on c.house_id=h.id left join sys_user u on c.user_id=u.id where a.trade_direction='1' and a.trans_status='2' and a.del_flag='0' and c.id is not null
AND c.property_project_id = #{propertyProject.id}
AND c.building_id = #{building.id}
AND c.house_id = #{house.id}
AND a.start_date >= str_to_date(#{startDate},'%Y-%m-%d')
AND a.expired_date <= str_to_date(#{expiredDate},'%Y-%m-%d')
)t ) tt group by project_name,building_name,house_no

select sales, project_name, building_name, house_no, start_date, expired_date, trans_amount, payment_type from (select sales, project_name, building_name, house_no, start_date, expired_date, trans_amount, payment_type from (select u.name sales, p.PROJECT_NAME, b.BUILDING_NAME, h.HOUSE_no, a.start_date, a.expired_date, a.trans_amount, a.payment_type from t_payment_trans a left join t_rent_contract c on a.trans_id = c.id left join T_PROPERTY_PROJECT p on c.PROPERTY_PROJECT_ID = p.id left join T_BUILDING b on c.BUILDING_ID = b.id left join t_house h on c.house_id = h.id left join sys_user u on c.user_id = u.id where a.trade_direction = '1' and a.trans_status = '2' and a.del_flag = '0' and a.payment_type = '6' and c.id is not null
AND c.property_project_id = #{propertyProject.id}
AND c.building_id = #{building.id}
AND c.house_id = #{house.id}
AND a.start_date >= str_to_date(#{startDate},'%Y-%m-%d')
AND a.expired_date <= str_to_date(#{expiredDate},'%Y-%m-%d')
) t) tt order by project_name,building_name,house_no

select project_name, building_name, house_no, c.contract_code, u.name sales, c.rental, c.start_date, c.expired_date, coalesce(t1.trade_amount,0) trade_amount, coalesce(t1.trans_amount,0) trans_amount, coalesce(t1.last_amount,0) last_amount from t_rent_contract c left join (select t.trans_id, sum(trade_amount) trade_amount, sum(t.trans_amount) trans_amount, sum(t.last_amount) last_amount from t_payment_trans t group by t.trans_id) t1 on c.id = t1.trans_id left join T_PROPERTY_PROJECT p on c.PROPERTY_PROJECT_ID = p.id left join T_BUILDING b on c.BUILDING_ID = b.id left join t_house h on c.house_id = h.id left join sys_user u on c.user_id = u.id
AND c.property_project_id = #{propertyProject.id}
AND c.building_id = #{building.id}
AND c.house_id = #{house.id}
